package main

import "fmt"

func FindFloor(instructions string) (bool, int) {
	if len(instructions) < 1 {
		return false, 0
	}
	floor := 0

	instrMap := map[string]int{
		"(": 1,
		")": -1,
	}

	for _, str := range instructions {
		if val, found := instrMap[string(str)]; found {
			floor += val
		} else {
			return false, 0
		}
	}
	return true, floor
}

